let currentSlide=0;function changeSlide(index){currentSlide=index;updateSlider()}function updateSlider(){const slides=document.querySelector(".slides");const slideWidth=document.querySelector(".slide").offsetWidth;slides.style.transform=`translateX(${-currentSlide*slideWidth}px)`}setInterval(()=>{if(currentSlide<5){currentSlide++}else{currentSlide=0}updateSlider()},3e3);const menuMobile=document.getElementById("btn-mobile");function abrirMenu(){const menu=document.getElementById("nav");menu.classList.toggle("ativo")}menuMobile.addEventListener("click",abrirMenu);